import logo from "./logo.svg";
import "./App.css";
import { render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";

function Square({ value, onSquareClick }) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

export default function Board() {
  const [rows, setRows] = useState(3);
  const [cols, setCols] = useState(3);

  const [xNext, setXNext] = useState(true);
  const [squares, setSquares] = useState(() => {
    return Array(rows)
      .fill(null)
      .map(() => Array(cols).fill(null)); // Dynamic initialization based on rows and cols
  });
  const [winner, setWinner] = useState(false);

  function calculateWinner(squares) {
    if (squares === undefined) {
      return;
    }

    for (let i = 0; i < squares.length - 1; i++) {
      const set = new Set(squares[i]);
      if (set.size == 1 && !set.has(null)) {
        console.log("Win");
        setWinner(true);
        return;
      }

      let colset = new Set();
      for (let j = 0; j < squares[i].length; j++) {
        console.log(squares[i][j]);
        colset.add(squares[i][j]);
      }
      console.log("loop");
      console.log(colset);
      console.log("Size:", colset.size);

      if (colset.size == 1 && !colset.has(null)) {
        setWinner(true);
        return;
      }
    }

    // for (let i = 0; i < squares[0].length; i++) {
    //   let colset = new Set();
    //   for (let j = 0; j < squares.length; j++) {
    //     console.log(squares[j][i]);
    //     colset.add(squares[j][i]);
    //   }
    //   console.log("loop");
    //   console.log(colset);
    //   console.log("Size:", colset.size);

    //   if (colset.size == 1 && !colset.has(null)) {
    //     setWinner(true);
    //     return;
    //   }
    // }

    let diagonalleft = new Set();
    let diagonalright = new Set();
    for (let i = 0; i < squares.length; i++) {
      diagonalleft.add(squares[i][i]);
      diagonalright.add(squares[i][squares[0].length - 1 - i]);
    }
    if (
      (diagonalleft.size == 1 && !diagonalleft.has(null)) ||
      (diagonalright.size == 1 && !diagonalright.has(null))
    ) {
      setWinner(true);
      return;
    }
    setWinner(false);
  }

  function onClickListener(i, j) {
    const newsquares = squares.slice();
    console.log(newsquares[i][j]);

    if (newsquares[i][j] || winner) return;

    newsquares[i][j] = xNext ? "X" : "O";
    calculateWinner(newsquares);
    console.log(winner);

    console.log(newsquares);
    setSquares(newsquares);
    setXNext(!xNext);
  }

  function clearBoard() {
    setWinner(false);
    let arr = [];
    for (var i = 1; i < rows; i++) {
      let array2 = [];
      for (var j = 1; j < cols; j++) {
        array2.push(null);
      }
      arr.push(array2);
    }
    console.log(arr);
    setSquares(arr);
  }

  function formSubmit(e) {
    e.preventDefault();
    console.log(rows, cols);
    var arr = [];
    for (var i = 0; i < rows; i++) {
      var array2 = [];
      for (var j = 0; j < cols; j++) {
        array2.push(null);
      }
      arr.push(array2);
    }
    console.log(arr);
    setSquares(arr);
  }

  return (
    <>
      {winner ? <h1>Winner: {xNext ? "O" : "X"}</h1> : ""}
      {squares.map((col, i) => (
        <div className="board-row">
          {col.map((value, j) => (
            <Square
              key={i + j}
              value={value}
              onSquareClick={() => onClickListener(i, j)}
            />
          ))}
        </div>
      ))}
      <h5 style={{ marginTop: 10 }}>{xNext ? "X" : "O"}'s turn</h5>

      <button onClick={clearBoard} style={{ marginTop: 5, marginLeft: 10 }}>
        Go Back
      </button>

      <button onClick={clearBoard} style={{ marginTop: 5, marginLeft: 10 }}>
        Clear Board
      </button>

      <form
        onSubmit={(e) => {
          formSubmit(e);
        }}
        style={{ marginTop: 10 }}
      >
        <label>Set Number of Rows: </label>
        <input type="number" onChange={(e) => setRows(e.target.value)}></input>
        <br />
        <label>Set Number of Columns: </label>
        <input type="number" onChange={(e) => setCols(e.target.value)}></input>
        <br />
        <button type="submit">Apply Changes</button>
      </form>
    </>
  );
}

