import "./Calculator.css";
import { getByDisplayValue, render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";

export default function Calculator() {
  const operators = ["+", "-", "*", "/", "%"];
  const calculatorLayout = [
    ["C", "AC", "%", "/"],
    ["1", "2", "3", "+"],
    ["4", "5", "6", "-"],
    ["7", "8", "9", "*"],
    ["0", ".", "Enter"],
  ];
  const [inputVal, setInputVal] = useState("3");

  function handleInput(val) {
    var stringVal = val + "";

    operators.forEach((operator) => {
      console.log(operator, stringVal.trim() === operator);

      console.log("value:", inputVal.trim().includes(operator));
      const index = inputVal.indexOf(operator);
      const beforeVal =
        index !== -1 && index > 0 ? inputVal.charAt(operator) : null;
      if (Number(beforeVal) != NaN) {
        setInputVal(inputVal + val);
      }
    });
  }

  function handleEnter() {}

  function handleAC() {
    setInputVal("");
  }

  function handleC() {
    if (inputVal !== "") {
      const newInput = inputVal.slice(0, -1);
      setInputVal(newInput);
    }
  }

  return (
    <>
      <h1>Calculator</h1>

      <table className="cal">
        <tbody>
          <tr>
            <td colSpan={4}>{inputVal}</td>
          </tr>

          {calculatorLayout.map((row, i) => (
            <tr key={i}>
              {row.map((value, j) => (
                <td key={j}>
                  <button
                    onClick={() => {
                      switch (value) {
                        case "Enter":
                          return handleEnter();
                        case "AC":
                          return handleAC();
                        case "C":
                          return handleC();
                        default:
                          console.log("input");
                          return handleInput(value);
                      }
                    }}
                  >
                    {value}
                  </button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
