import "./Calculator.css";
import { getByDisplayValue, render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";
import { useInRouterContext } from "react-router-dom";

export default function Calculator() {
  const operators = ["+", "-", "*", "/", "%"];
  const calculatorLayout = [
    ["C", "AC", "%", "/"],
    ["1", "2", "3", "+"],
    ["4", "5", "6", "-"],
    ["7", "8", "9", "*"],
    ["0", ".", "Enter"],
  ];
  const [inputVal, setInputVal] = useState("3");

  function handleInput(val) {
    var stringVal = val + "";
    let isValidInput = true;

    if (operators.includes(val)) {
      if (isNaN(Number(inputVal[inputVal.length - 1]))) {
        isValidInput = false;
      }
    }

    if (val == "." && isNaN(Number(inputVal[inputVal.length - 1]))) {
      isValidInput = false;
    } else if (inputVal.includes(".")) {
      const hasOperator = operators.some((operator) =>
        inputVal.includes(operator)
      );
      if (hasOperator) {
      }
    }

    if (isValidInput) {
      setInputVal(inputVal + val);
    }
  }

  function handleEnter() {}

  function handleAC() {
    setInputVal("");
  }

  function handleC() {
    if (inputVal !== "") {
      const newInput = inputVal.slice(0, -1);
      setInputVal(newInput);
    }
  }

  return (
    <>
      <h1>Calculator</h1>

      <table className="cal">
        <tbody>
          <tr>
            <td colSpan={4}>{inputVal}</td>
          </tr>

          {calculatorLayout.map((row, i) => (
            <tr key={i}>
              {row.map((value, j) => (
                <td key={j}>
                  <button
                    onClick={() => {
                      switch (value) {
                        case "Enter":
                          return handleEnter();
                        case "AC":
                          return handleAC();
                        case "C":
                          return handleC();
                        default:
                          return handleInput(value);
                      }
                    }}
                  >
                    {value}
                  </button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
