import "./Calculator.css";
import { getByDisplayValue, render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";

export default function Calculator() {
  const operators = ["+", "-", "*", "/"];
  const calculatorLayout = [
    ["C", "AC", "", "/"],
    ["7", "8", "9", "*"],
    ["4", "5", "6", "-"],
    ["1", "2", "3", "+"],
    ["0", ".", "Enter"],
  ];
  const [inputVal, setInputVal] = useState();

  function handleInput(val) {
    let currInput = inputVal;
    operators.forEach((operator) => {
      if (currInput.includes(operator)) {
        return;
      }
    });
    currInput = currInput + val;
    setInputVal(currInput);
  }

  function handleEnter() {}

  function handleAC() {}

  function handleC() {}

  return (
    <>
      <h1>Calculator</h1>

      <table className="cal">
        <tr>
          <td colSpan={4}>{inputVal}</td>
        </tr>
        <tbody>
          {calculatorLayout.map((row) => (
            <tr>
              {row.map((value) => {
                <td>
                  <button>{value}</button>
                </td>;
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
