import "./Calculator.css";
import { getByDisplayValue, render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";

export default function Calculator() {
  const operators = ["+", "-", "*", "/"];

  const calOperators = operators.map((v, i) => (
    <>
      {i > 3 ? (
        ""
      ) : (
        <td>
          <button>{v}</button>
        </td>
      )}
    </>
  ));

  return (
    <>
      <h1>Calculator</h1>
      <table className="cal">
        <tr>{calOperators}</tr>

        {Array(3)
          .fill(null)
          .map((v, j) => (
            <tr>
              {Array(3)
                .fill(null)
                .map((v, i) => (
                  <td>
                    <button key={i + 1 + j * 3}>{i + 1 + j * 3}</button>
                  </td>
                ))}
            </tr>
          ))}

        <tr>
          <td>
            <button>+</button>
          </td>
          <td>
            <button>-</button>
          </td>
          <td>
            <button>*</button>
          </td>
        </tr>
      </table>
    </>
  );
}
