import { Outlet, Link } from "react-router-dom";

const linkStyle = {
  textDecoration: "none",
  border: "solid black 1px",
  padding: 10,
  color: "blueviolet",
  fontWeight: "bold",
  borderRadius: 5,
};

export default function Layout() {
  return (
    <>
      <nav style={{ display: "flex", alignContent: "space-around" }}>
        <ul>
          <li
            style={{
              display: "inline-block",
              listStyleType: "none",
              padding: 0,
              margin: 0,
              marginLeft: -20,
              marginBottom: 20,
            }}
          >
            <Link to={"/"} style={linkStyle}>
              Tic-Tac-Toe
            </Link>
          </li>
          <li style={{ display: "inline-block", marginLeft: 10 }}>
            <Link to={"/calc"} style={{ linkStyle }}>
              Calculator
            </Link>
          </li>
        </ul>
      </nav>
      <hr style={{ marginBottom: 20 }} />
      <Outlet></Outlet>
    </>
  );
}
