import "./Calculator.css";
import { getByDisplayValue, render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";

export default function Calculator() {
  const operators = ["+", "-", "*", "/"];
  const [inputVal, setInputVal] = useState("");

  const calOperatorsTop = operators.map((v, i) => (
    <>
      {i >= 3 ? (
        ""
      ) : (
        <td>
          <button>{v}</button>
        </td>
      )}
    </>
  ));

  const calNums = Array(3)
    .fill(null)
    .map((v, j) => (
      <tr>
        {Array(3)
          .fill(null)
          .map((v, i) => (
            <td>
              <button key={i + 1 + j * 3}>{i + 1 + j * 3}</button>
            </td>
          ))}
      </tr>
    ));

  return (
    <>
      <h1>Calculator</h1>

      <table className="cal">
        <tr>
          <td colSpan={3}>{inputVal}</td>
        </tr>
        <tr>{calOperatorsTop}</tr>
        {calNums}

        <tr>
          <td>
            <button>/</button>
          </td>
          <td colSpan={2}>
            <button className="enter">Enter</button>
          </td>
        </tr>
      </table>
    </>
  );
}
