import logo from "./logo.svg";
import "./App.css";
import { render } from "@testing-library/react";
import { useReducer, useState } from "react";

const rows = 3;

function Square({ value, onSquareClick }) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

export default function Board() {
  const [xNext, setXNext] = useState(true);
  const [squares, setSquares] = useState(
    Array(3)
      .fill(0)
      .map((row) => Array(3).fill(null))
  );
  const [winner, setWinner] = useReducer((winner) => !winner, false);

  function calculateWinner(squares) {
    squares.forEach((element) => {
      if (new Set(element).size == 1 && element[1] != null) {
        console.log("Win");
        setWinner();
      }
    });
  }

  function onClickListener(i, j) {
    const newsquares = squares.slice();
    console.log(newsquares[i][j]);

    if (newsquares[i][j]) return;
    if (winner) return;
    newsquares[i][j] = xNext ? "X" : "O";
    calculateWinner(newsquares);
    console.log(winner);

    console.log(newsquares);
    setSquares(newsquares);
    setXNext(!xNext);
  }

  return (
    <>
      {squares.map((col, i) => (
        <div className="board-row">
          {col.map((value, j) => (
            <Square
              key={i + j}
              value={value}
              onSquareClick={() => onClickListener(i, j)}
            />
          ))}
        </div>
      ))}
    </>
  );
}

