import "./Calculator.css";
import { getByDisplayValue, render } from "@testing-library/react";
import { useEffect, useReducer, useState } from "react";
import { useInRouterContext } from "react-router-dom";

export default function Calculator() {
  const operators = ["+", "-", "*", "/", "%"];
  const calculatorLayout = [
    ["C", "AC", "%", "/"],
    ["1", "2", "3", "+"],
    ["4", "5", "6", "-"],
    ["7", "8", "9", "*"],
    ["0", ".", "Enter"],
  ];
  const [inputVal, setInputVal] = useState("");

  function handleInput(val) {
    let isValidInput = true;

    if (operators.includes(val)) {
      if (isNaN(Number(inputVal[inputVal.length - 1]))) {
        isValidInput = false;
      }
    }

    if (val === ".") {
      if (isNaN(Number(inputVal[inputVal.length - 1]))) {
        isValidInput = false;
      }

      let operatorIndex = -1;
      operators.forEach((operator) => {
        let index = inputVal.lastIndexOf(operator);
        if (index !== -1 && index <= inputVal.length) {
          operatorIndex = index;
        }
      });

      if (operatorIndex !== -1) {
        const num = inputVal.slice(inputVal.indexOf(operatorIndex));
        console.log("num", num);
        if (num.includes(".")) {
          isValidInput = false;
        }
      } else if (inputVal.includes(".")) {
        isValidInput = false;
      }
    }

    if (isValidInput) {
      setInputVal(inputVal + val);
    }
  }

  function handleEnter() {
    var operatorsInInput = [];
    var numbersInInput = [];

    for (let i = 0; i < inputVal.length; i++) {
      const char = inputVal[i];

      operators.forEach((operator) => {
        if (char === operator) {
          if (i == inputVal.length - 1) {
            operatorsInInput = [];
            return;
          }
          operatorsInInput.push(operator);
        }
      });
    }

    console.log(operatorsInInput);

    if (operatorsInInput.length != 0) {
    }
  }

  function handleAC() {
    setInputVal("");
  }

  function handleC() {
    if (inputVal !== "") {
      const newInput = inputVal.slice(0, -1);
      setInputVal(newInput);
    }
  }

  return (
    <>
      <h1>Calculator</h1>

      <table className="cal">
        <tbody>
          <tr>
            <td colSpan={4} className="displayInput">
              {inputVal}
            </td>
          </tr>

          {calculatorLayout.map((row, i) => (
            <tr key={i}>
              {row.map((value, j) => (
                <td key={j}>
                  <button
                    onClick={() => {
                      switch (value) {
                        case "Enter":
                          return handleEnter();
                        case "AC":
                          return handleAC();
                        case "C":
                          return handleC();
                        default:
                          return handleInput(value);
                      }
                    }}
                  >
                    {value}
                  </button>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
