import logo from "./logo.svg";
import "./App.css";
import { render } from "@testing-library/react";
import { useReducer, useState } from "react";

function Square({ value, onSquareClick }) {
  return (
    <button className="square" onClick={onSquareClick}>
      {value}
    </button>
  );
}

export default function Board() {
  const [xNext, setXNext] = useState(true);
  const [squares, setSquares] = useState(
    Array(rows)
      .fill(0)
      .map((row) => Array(cols).fill(null))
  );
  const [winner, setWinner] = useState(false);
  const [rows, setRows] = useState(3);
  const [cols, setCols] = useState(3);

  function calculateWinner(squares) {
    for (let i = 0; i < squares.length; i++) {
      const set = new Set(squares[i]);
      if (set.size == 1 && !set.has(null)) {
        console.log("Win");
        setWinner(true);
        return;
      }

      let colset = new Set();
      for (let j = 0; j < squares[0].length; j++) {
        colset.add(squares[j][i]);
        console.log(j, i);
      }
      console.log("loop");
      console.log(colset);
      console.log("Size:", colset.size);
      if (colset.size == 1 && !colset.has(null)) {
        setWinner(true);
        return;
      }
    }

    let diagonalleft = new Set();
    let diagonalright = new Set();
    for (let i = 0; i < squares[0].length; i++) {
      diagonalleft.add(squares[i][i]);
      diagonalright.add(squares[i][squares[0].length - 1 - i]);
    }
    if (
      (diagonalleft.size == 1 && !diagonalleft.has(null)) ||
      (diagonalright.size == 1 && !diagonalright.has(null))
    ) {
      setWinner(true);
      return;
    }
  }

  function onClickListener(i, j) {
    const newsquares = squares.slice();
    console.log(newsquares[i][j]);

    if (newsquares[i][j] || winner) return;

    newsquares[i][j] = xNext ? "X" : "O";
    calculateWinner(newsquares);
    console.log(winner);

    console.log(newsquares);
    setSquares(newsquares);
    setXNext(!xNext);
  }

  function clearBoard() {
    window.location.reload();
  }

  return (
    <>
      {winner ? <h1>Winner: {xNext ? "O" : "X"}</h1> : ""}
      {squares.map((col, i) => (
        <div className="board-row">
          {col.map((value, j) => (
            <Square
              key={i + j}
              value={value}
              onSquareClick={() => onClickListener(i, j)}
            />
          ))}
        </div>
      ))}
      <button onClick={clearBoard} style={{ marginTop: 10 }}>
        Clear Board
      </button>

      <form>
        <label>Set Number of Rows: </label>
        <input type="text" onChange={(e) => setRows(e.target.value)}></input>
        <label>Set Number of Columns: </label>
        <input type="text" onChange={(e) => setCols(e.target.value)}></input>
        <button type="submit">Apply Changes</button>
      </form>
    </>
  );
}

